import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  list: [
    { id: 1, name: 'Alice', letter: 'A' },
    { id: 2, name: 'Charlie', letter: 'C' },
    { id: 3, name: 'David', letter: 'D' },
  ],
  selectedLetter: 'A',
}

export const usersSlice = createSlice({
  name: 'users',
  initialState,
  reducers: {
    setSelectedLetter: (state, action) => {
      state.selectedLetter = action.payload
    },
    addUser: (state, action) => {
      state.list.push(action.payload)
    },
  },
})

export const { setSelectedLetter, addUser } = usersSlice.actions

// Селекторы
export const selectAllUsers = (state) => state.users.list
export const selectSelectedLetter = (state) => state.users.selectedLetter
export const selectUsersByLetter = (state) => {
  const selectedLetter = state.users.selectedLetter
  return state.users.list.filter((user) => user.letter === selectedLetter)
}

export default usersSlice.reducer
